"use strict";

var br = require('br/Core');
var BundlePathService = require('br/services/BundlePathService');

/**
 * @name br.services.BRBundlePathService
 * @constructor
 * @class This class provides access to the App version which is automatically generated by BladeRunnerJS
 *
 * @implements br.services.BundlePathService
 */
function BRBundlePathService() {
};

BRBundlePathService.prototype.getBundlePath = function(bundlePath)	{
	if (bundlePath != undefined) {
		/* make sure there are no leading /s that might mess up the generated path */
		while(bundlePath.charAt(0) === '/') { 
			bundlePath = bundlePath.substr(1); 
		}
		return window.$BRJS_BUNDLE_PATH + "/" + bundlePath
	}
	return window.$BRJS_BUNDLE_PATH;
}

br.implement(BRBundlePathService, BundlePathService);

module.exports = BRBundlePathService;
